mapper.select:
SELECT PRIV_CD
FROM employees
	WHERE EMP_EMAIL = #{id}

mapper.update:
UPDATE	employees 
	SET IDX = #{idx}
		,EMP_NUM = #{emp_num}
		,EMP_NAME = #{emp_name}
		,DEPT_CD = #{dept_cd}
		,GRADE_CD = #{grade_cd}
		,EMP_TEL = #{emp_tel}
		,EMP_DTEL = #{emp_dtel}
		,EMP_EMAIL = #{emp_email}
		,EMP_PASSWD = #{emp_passwd}
		,EMP_POST_NO = #{emp_post_no}
		,EMP_ADDR = #{emp_addr}
		,HIRE_DATE = #{hire_date}
		,WORK_CD = #{work_cd}
		,PRIV_CD = #{priv_cd}
		,PHOTO = #{photo}
	WHERE EMP_EMAIL = #{emp_email}
