mapper.update:
UPDATE stock 
	SET STOCK_QTY = #{qty}
		WHERE STOCK_CD = #{stock_cd}

mapper.select:
SELECT emp_num 
	FROM employees
	WHERE emp_email = #{sId}

mapper.insert:

	SELECT PRODUCT_CD 	
		FROM stock 
		WHERE STOCK_CD = #{stock.stock_cd}

INSERT INTO stock_history
	VALUES (
		STOCK_CD = #{stock.stock_cd} 
	    ,STOCK_CONTROL_TYPE_CD = #{stock.stock_control_type_cd}
	    ,PRODUCT_CD = #{PRODUCT_CD}
	    ,SOURCE_STOCK_CD = #{stock.source_stock_cd}
	    ,TARGET_STOCK_CD = #{stock.target_stock_cd}
	    ,QTY = #{stock.qty}
		,EMP_NUM = #{stock.emp_num}
		,now()
		,REMARKS = ""
	)

mapper.select:
SELECT * 
FROM stock_view
	
	
	
	
	
		
			WHERE STOCK_CD LIKE '%${keyword}%'
		
		
			WHERE PRODUCT_NAME LIKE '%${keyword}%'
		
		
		
			
	
	
	
		WHERE wh_cd = #{wh_cd}
		
			AND wh_area_cd = #{wh_area_cd}
			
				AND wh_loc_in_area_cd = #{wh_loc_in_area_cd}
			
		
	
	
	LIMIT #{startRow}, #{listLimit}
