mapper.insert:

	SELECT MAX(idx) FROM employees

INSERT 
	INTO employees
		VALUES (


				1


				(#{idx} + 1)




				,1


				, #{emp_num}


			,#{emp_name}
			,#{dept_cd}
			,#{grade_cd}
			,#{emp_tel}
			,#{emp_dtel}
			,#{emp_email}
			,#{emp_passwd}
			,#{emp_post_No}
			,#{emp_addr}
			,#{hire_date}
			,#{work_cd}
			,#{priv_cd}
			,#{photo}

		);

mapper.select:
SELECT DEPT_CD
	FROM department
		WHERE DEPT_NAME = #{dept_cd}

SELECT emp_passwd
	FROM employees
	WHERE emp_email = #{emp_email}

SELECT PRIV_CD
FROM employees
	WHERE EMP_EMAIL = #{id}

mapper.update:
UPDATE	employees 
	SET IDX = #{idx}
		,EMP_NUM = #{emp_num}
